package activities.controller; import javax.servlet.*; import javax.servlet.http.*; import java.io.IOException; public class AuthFilter implements Filter { public void doFilter(ServletRequest request, ServletResponse response, FilterChain chain) throws IOException, ServletException { HttpServletRequest req = (HttpServletRequest) request; HttpServletResponse res = (HttpServletResponse) response; HttpSession session = req.getSession(false); if (session == null || session.getAttribute("login") == null) { res.sendRedirect("index.jsp"); return; } chain.doFilter(request, response); } public void init(FilterConfig config) {} public void destroy() {} }